// Process successful scraping results
const inputData = $input.first();

if (!inputData || !inputData.json) {
  throw new Error('No input data received from API');
}

const response = inputData.json;
const listings = Array.isArray(response.listings) ? response.listings : [];
const metadata = response.metadata || {};
const statistics = response.statistics || {};

const message = `✅ Scraped ${listings.length} listings successfully at ${response.scraped_at || 'N/A'}\n` +
  `Google Sheets: ${response.google_sheets_saved ? 'Saved' : 'Not saved'}\n` +
  `Statistics: ${JSON.stringify(statistics, null, 2)}`;

return [
  {
    json: {
      success: true,
      listingsCount: listings.length,
      scrapedAt: response.scraped_at,
      googleSheetsSaved: response.google_sheets_saved || false,
      statistics: statistics,
      metadata: metadata,
      message: message,
      listings: listings
    }
  }
];

// Format error response
const inputData = $input.first();

if (!inputData || !inputData.json) {
  throw new Error('No input data received from API');
}

const response = inputData.json;
const error = response.error || 'Unknown error';
const traceback = response.traceback || '';

const message = `❌ Scraper failed\n\nError: ${error}\n\nTraceback:\n${traceback}`;

return [
  {
    json: {
      success: false,
      error: error,
      traceback: traceback,
      message: message
    }
  }
];
